	}

	if(args[0].toLowerCase() != "install" &&
		args[0].toLowerCase() != "uninstall" &&
		args[0].toLowerCase() != "list") {

		callback();

		return;
	}
	
	aceUtils = require("kaeon-united")("aceUtils");
	execSync = require('child_process').execSync;
	fs = require("fs");
	sep = require("path").sep;
	
