			});
			
			fs.writeFileSync(
				process.cwd() + "/use.json",
				JSON.stringify({ use: useFile })
			);
		}
	
		else if(operation == "list")
			console.log(Object.keys(useFile.entities).join("\n"));

		callback();
	})();
