	for(let i = 0; i < tokens.length; i++) {

		if(tokens[i] == "\"")
			inQuote = !inQuote;

		else if(tokens[i] == "\\") {

			args[args.length - 1] += tokens[i + 1];

			i++;
		}

		else if(tokens[i] == " " && !inQuote)
			args.push("");

		else
			args[args.length - 1] += tokens[i];
	}

	return args;
}

