		'html': 'text/html',
		'js': 'text/javascript',
		'json': 'application/json',
		'css': 'text/css',
		'png': 'image/png',
		'jpg': 'image/jpeg',
		'wav': 'audio/wav',
		'mp3': 'audio/mpeg',
		'svg': 'image/svg+xml',
		'pdf': 'application/pdf',
		'doc': 'application/msword',
		'mp4': 'video/mp4'
	};

	let fileTypes = [
		'ico',
		'png',
		'jpg',
