}

function processRequest(request, protocol, callback) {

	let text = null;

	request.on('data', (chunk) => {
		text = (text != null ? text : "") + chunk.toString();
	}).on('end', () => {
		callback({
			request: {
				method: request.method,
				uri: protocol +
					"://" +
					request.headers.host +
					request.url
			},
			headers: request.headers,
			body: text
		});
	});
}

