		let dataBegin = data.indexOf("# BEGIN WIFI DATA");
		let dataEnd = data.indexOf("# END WIFI DATA");
	
		let dataSettings = "# BEGIN WIFI DATA\n" +
			"interface wlan0\n" +
			"    static ip_address=" +
			(credentials != null ?
				(credentials.ip != null ?
					credentials.ip :
