var moduleDependencies = {
	cache: "https://cdn.jsdelivr.net/npm/kaeon-united/localCache.json",
	cors: {
		utils: {
			formatGithubURI: (uri) => {

				uri = uri.substring(34);
				let user = uri.substring(0, uri.indexOf("/"));

				uri = uri.substring(uri.indexOf("/") + 1);
				let repo = uri.substring(0, uri.indexOf("/"));

				uri = uri.substring(uri.indexOf("/") + 1);

				return "https://cdn.jsdelivr.net/gh/" +
					user +
					"/" +
					repo +
					"/" +
					uri.substring(uri.indexOf("/") + 1);
			}
		},
		proxies: {
			"https://corsproxy.io/": (request) => {

				request = JSON.parse(JSON.stringify(request));

				if(request.request.uri.startsWith(
					"https://raw.githubusercontent.com/")) {

					request.request.uri =
						moduleDependencies.cors.utils.formatGithubURI(
							request.request.uri
						);
				}

				else {
				
					request.request.uri =
						"https://corsproxy.io/?" +
							encodeURIComponent(
								request.request.uri
							).split("%20").join("%2520");
				}

				return request;
			},
			"https://nextjs-cors-anywhere.vercel.app/": (request) => {

				request = JSON.parse(JSON.stringify(request));

				if(request.request.uri.startsWith(
					"https://raw.githubusercontent.com/")) {

					request.request.uri =
						moduleDependencies.cors.utils.formatGithubURI(
							request.request.uri
						);
				}

				else {
					
					request.request.uri =
						"https://nextjs-cors-anywhere.vercel.app/api?endpoint=" +
							request.request.uri;
				}

				return request;
			}
		},
		proxy: "https://corsproxy.io/"
	},
	utilities: "https://raw.githubusercontent.com/Atlas-of-Kaeon/Atlas-of-Kaeon.github.io/master/Kaeon%20United/3%20-%20Wonders/2%20-%20Source/1%20-%20Code/1%20-%20Interface/2%20-%20Singularities/2%20-%20Utilities/kaeonUnitedSingularityUtilities.js"
};

function executeSingularity() {

	localStorage.setItem("localCache", openResource(moduleDependencies.cache));

	if(typeof require != typeof undefined) {

		if(require.kaeonUnited)
			return;
	}

	module = {
		id: '.',
		exports: { },
		parent: null,
		filename: "",
		loaded: false,
		children: [],
		paths: []
	};
	
	function unitedRequire(path, options) {

		if(typeof options != "object")
			options = { };

		if(options.async == true || typeof options.async == "function") {

			let promise = new Promise(function(resolve, reject) {

				try {

					resolve(
						require(
							path,
							{
								dynamic: options.dynamic,
								global: options.global,
								reload: options.reload
							}
						)
					);
				}

				catch(error) {
					reject(error);
				}
			});

			if(options.async != true)
				promise.then(options.async);

			return options.async == true ? promise : undefined;
		}

