}

function mark3(x, y) {

	let cos = Math.cos(Math.PI / 6) / 3;
	let sin = Math.sin(Math.PI / 6) / 3;

	let m = (sin + (1 / 3)) / cos;
	
	return y <= (1 / 3) + (x * m) + .02 &&
		y >= (1 / 3) + (x * m) - .01 &&
		y >= (-sin - .01) &&
		x <= 0;
}

function mark4(x, y) {

	let cos = Math.cos(Math.PI / 6) / 3;
	let sin = Math.sin(Math.PI / 6) / 3;

	let m = (sin + (1 / 3)) / cos;
	
	return y <= (1 / 3) + (x * -m) + .02 &&
